<?php	class Jump{		public function Jump($url = "success.php",$param = "")	{		$pstring = "?";		if($param=="")		  {		    header("location:$url");		  }		else		  {		    foreach($param as $key => $val)		      {			$pstring.="$key=$val&";		      }		    $pstring = substr($pstring, -1);		    header("location:$url".$pstring);		  }	}}